import React from "react";

function Locacion({ locacion, seleccionarLocacion, agregarALocacionesGuardadas }) {
  const { id, nombre, descripcion, galeria, localizacion, locacionSeleccionada } = locacion;

  const handleSeleccionarLocacion = () => {
    seleccionarLocacion(id);
  };

  const handleAgregarALocacionesGuardadas = () => {
    agregarALocacionesGuardadas(locacion);
  };

  return (
    <div className={`locacion ${locacionSeleccionada ? 'seleccionada' : ''}`}>
      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      <div className="galeria">
        {galeria.map((foto, index) => (
          <img key={index} src={foto} alt={`Foto ${index + 1}`} />
        ))}
      </div>
      <p>Localizaci√≥n: {localizacion}</p>
      <button onClick={handleSeleccionarLocacion}>Seleccionar</button>
      <button onClick={handleAgregarALocacionesGuardadas}>Agregar a locaciones guardadas</button>
    </div>
  );
}

export default Locacion;
